// User Interface variables.
const form = document.querySelector("form");
const selCards = document.querySelector("#selCards");
const choices = document.querySelector("#choices");
const hand = document.querySelector("#hand");
const played = document.querySelector("#played");
const messanger = document.querySelector("#messanger");

// Setup select list with cards.
Cards.forEach((card, index) => {
  let option = document.createElement("option");
  option.text = card.title;
  option.value = `${index}`;
  selCards.appendChild(option);
}); // end forEach Cards.

const createCard = function (card) {
  let div = document.createElement("div");
  const title = `${card.title}`;
  const textArray = card.text.map((item) => {
    return `<div>${item}</div>`;
  }); // end map card.text.
  const text = textArray.join('\n');
  div.innerHTML = `
  <h3>${title}</h3>
  <div>${text}</div>
  <div class="actions">
    <button data-action="play" class="hide" disabled>Play ${card.title}</button>
    <button data-action="keep">Keep ${card.title}</button>
    <button data-action="discard">Discard ${card.title}</button>
  </div>`;
  return div;
} // end createCard.

const cardActions = function (event) {
  const action = event.target.getAttribute("data-action");
  let actions = event.target.parentNode;
  let card = actions.parentNode;
  const title = card.querySelector("h3").textContent;
  
  switch (action) {
    case "play":
      sendMessage(`Playing ${title} card.`);
      actions.querySelector(`[data-action="play"]`).remove();
      played.appendChild(card);
    break;
    case "keep":
      sendMessage(`Adding ${title} to hand.`);
      let btnPlay = actions.querySelector(`[data-action="play"]`);
      let btnKeep = actions.querySelector(`[data-action="keep"]`);
      btnPlay.className = "";
      btnPlay.removeAttribute("disabled");
      btnKeep.remove();
      hand.appendChild(card);
    break;
    case "discard":
      sendMessage(`${title} card discarded.`);
      card.remove();
    break;
    default:
  } // end switch action.
}; // end cardActions.

const sendMessage = function (text) {
  messanger.innerHTML = "";
  messanger.innerHTML = `${text}`;
}; // end sendMessange.
// Main.

form.addEventListener("submit", (event) => {
  const index = parseInt(selCards.value);
  if (Number.isInteger(index)) {
    let card = Cards[index];
    let cardNode = createCard(card);
    cardNode.addEventListener("click", cardActions);
    choices.appendChild(cardNode);
    sendMessage(`${card.title} card drawn from the deck.`);
  } // end if.

  event.preventDefault();
}); // end form submit event.